    isSuccess(message) {
        this.errorMessage = false;
        this.successMessage = message;
        this.success = true;
    }
    saveChanges() {
        let data = {
            stats: this.stats,
            identity: this.identity,
            notif: this.notifications,
        };
        this.usersService.updateUser(data).subscribe((res) => {
            if (res.success) {
                this.isSuccess('Settings saved successfully');
                this.usersService.logOut();
                return;
            }
            this.isError();
            return;
        }, (err) => {
            this.isError();
            return;
        });
    }
}
